#!/bin/bash

# ๐ Script de Configuraรงรฃo Inicial para VPS - Uptime Monitor
# Execute este script na VPS para preparar o ambiente para deploy automรกtico

set -e

echo "๐ Configurando VPS para Deploy Automรกtico..."

# Cores para output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Funรงรฃo para imprimir mensagens coloridas
print_status() {
    echo -e "${BLUE}[INFO]${NC} $1"
}

print_success() {
    echo -e "${GREEN}[SUCCESS]${NC} $1"
}

print_warning() {
    echo -e "${YELLOW}[WARNING]${NC} $1"
}

print_error() {
    echo -e "${RED}[ERROR]${NC} $1"
}

# Verificar se estรก rodando como root ou com sudo
if [[ $EUID -eq 0 ]]; then
    print_warning "Rodando como root. Recomendado usar usuรกrio normal com sudo."
fi

# 1. Atualizar sistema
print_status "Atualizando sistema..."
sudo apt update && sudo apt upgrade -y

# 2. Instalar dependรชncias bรกsicas
print_status "Instalando dependรชncias bรกsicas..."
sudo apt install -y curl wget git unzip software-properties-common apt-transport-https ca-certificates gnupg lsb-release

# 3. Instalar Docker se nรฃo estiver instalado
if ! command -v docker &> /dev/null; then
    print_status "Instalando Docker..."
    curl -fsSL https://download.docker.com/linux/ubuntu/gpg | sudo gpg --dearmor -o /usr/share/keyrings/docker-archive-keyring.gpg
    echo "deb [arch=$(dpkg --print-architecture) signed-by=/usr/share/keyrings/docker-archive-keyring.gpg] https://download.docker.com/linux/ubuntu $(lsb_release -cs) stable" | sudo tee /etc/apt/sources.list.d/docker.list > /dev/null
    sudo apt update
    sudo apt install -y docker-ce docker-ce-cli containerd.io
    
    # Adicionar usuรกrio ao grupo docker
    sudo usermod -aG docker $USER
    print_success "Docker instalado com sucesso!"
else
    print_success "Docker jรก estรก instalado."
fi

# 4. Instalar Docker Compose se nรฃo estiver instalado
if ! command -v docker-compose &> /dev/null; then
    print_status "Instalando Docker Compose..."
    sudo curl -L "https://github.com/docker/compose/releases/latest/download/docker-compose-$(uname -s)-$(uname -m)" -o /usr/local/bin/docker-compose
    sudo chmod +x /usr/local/bin/docker-compose
    print_success "Docker Compose instalado com sucesso!"
else
    print_success "Docker Compose jรก estรก instalado."
fi

# 5. Instalar Node.js se nรฃo estiver instalado
if ! command -v node &> /dev/null; then
    print_status "Instalando Node.js..."
    curl -fsSL https://deb.nodesource.com/setup_18.x | sudo -E bash -
    sudo apt install -y nodejs
    print_success "Node.js instalado com sucesso!"
else
    print_success "Node.js jรก estรก instalado."
fi

# 6. Configurar SSH para GitHub Actions
print_status "Configurando SSH para GitHub Actions..."

# Criar diretรณrio .ssh se nรฃo existir
mkdir -p ~/.ssh
chmod 700 ~/.ssh

# Verificar se jรก existe chave SSH
if [ ! -f ~/.ssh/id_rsa ]; then
    print_status "Gerando nova chave SSH..."
    ssh-keygen -t rsa -b 4096 -C "github-actions-deploy" -f ~/.ssh/id_rsa -N ""
    print_success "Chave SSH gerada!"
else
    print_success "Chave SSH jรก existe."
fi

# Adicionar chave pรบblica ao authorized_keys
cat ~/.ssh/id_rsa.pub >> ~/.ssh/authorized_keys
chmod 600 ~/.ssh/authorized_keys

# 7. Configurar Git (se necessรกrio)
print_status "Configurando Git..."
if [ -z "$(git config --global user.name)" ]; then
    read -p "Digite seu nome para o Git: " git_name
    git config --global user.name "$git_name"
fi

if [ -z "$(git config --global user.email)" ]; then
    read -p "Digite seu email para o Git: " git_email
    git config --global user.email "$git_email"
fi

# 8. Criar diretรณrio do projeto se nรฃo existir
read -p "Digite o caminho onde o projeto serรก clonado (ex: /home/usuario/uptime-monitor): " project_path

if [ ! -d "$project_path" ]; then
    print_status "Criando diretรณrio do projeto..."
    mkdir -p "$project_path"
    print_success "Diretรณrio criado: $project_path"
fi

# 9. Clonar repositรณrio se nรฃo existir
if [ ! -d "$project_path/.git" ]; then
    read -p "Digite a URL do repositรณrio Git (ex: https://github.com/usuario/repo.git): " repo_url
    print_status "Clonando repositรณrio..."
    git clone "$repo_url" "$project_path"
    print_success "Repositรณrio clonado!"
fi

# 10. Configurar firewall bรกsico
print_status "Configurando firewall..."
sudo ufw allow ssh
sudo ufw allow 80
sudo ufw allow 443
sudo ufw --force enable
print_success "Firewall configurado!"

# 11. Exibir informaรงรตes importantes
echo ""
echo "๐ Configuraรงรฃo concluรญda com sucesso!"
echo ""
echo "๐ Informaรงรตes importantes para configurar no GitHub:"
